from scapy.all import *

data = ""

## addr1 = MAC de destino (MAC da placa wireless)
## addr2 = Endereco MAC de origem do remetente. (MAC forjado)
## addr3 = Endereco MAC do ponto de acesso.
dot11 = Dot11(type=2, subtype=0, addr1='E4:18:6B:4B:94:00', addr2='77:77:77:77:77:77', addr3='33:33:33:33:33:33')

essid = Dot11Elt(ID='SSID',info='testSSID', len=len('testSSID')) # indica a capacidade do ponto de acesso

frame = RadioTap()/dot11/essid/data

frame.show()
 

sendp(frame, iface='wlp3s0mon', inter=1, loop=0, count=300) # iface = Nome da Interface Wireless. inter = intervalo entre o envio dos pacotes (em segundos). count = numero de pacotes 
